sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var now_time = new GlideDateTime();

    var overdue_task = new GlideRecord("x_960166_need_it_needit");
    overdue_task.addQuery("when_needed", '<', now_time);
    overdue_task.addQuery('state!=3');
    overdue_task.addQuery("u_reference_2=6816f79cc0a8016401c5a33be04be441");
    overdue_task.query();

    var count = overdue_task.getRowCount();
	if(count > 1){

		template.print("In addition to this overdue task, You have :" + (overdue_task.getRowCount() - 1) + ' ' + "more task in overdue ");

	}








})(current, template, email, email_action, event);
