sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	dateValidation : function(date){

		var now_date = new GlideDateTime();
		var today = new GlideDateTime(date);

		if(today.before(now_date)){
			return true ;
		}
		else{
			return false ;
		}

	},

	isToday : function(date){

		var now_date = new GlideDateTime();
		var my_date = new GlideDateTime(date);

		var is_date = now_date.getLocalDate();
		var is_today = my_date.getLocalDate();

		if(is_date.compareTo(is_today)== 0){
			return true;
		}
		else{
			return false ;
		}
	},

    type: 'NeedItUtils'
};
