sysauto_script.script:
var current_date = new GlideDateTime();

var record = new GlideRecord('x_960166_need_it_needit');
record.addQuery('when_needed','>=',current_date);
record.addQuery('when_needed','<',gs.hoursAgo(-24));
record.addQuery('state!=3');
record.query();

while(record.next()){
	gs.info('Task due date soon are :'+record.number);
	gs.eventQueue("x_960166_need_it.dueSoonNeedItTasks",record,record.number,gs.getUserName());

}

